INSERT INTO `acore_string` (`entry`, `content_default`, `locale_koKR`, `locale_frFR`, `locale_deDE`, `locale_zhCN`, `locale_zhTW`, `locale_esES`, `locale_esMX`, `locale_ruRU`) VALUES
(@ENTRY+0, 'You died during a hardcore mode session, so now you are a permanent ghost. Try to scare the living.', '', '', '', '', '', 'Moriste durante una sesion en modo hardcore, asi que ahora eres un fantasma permanente.Intenta asustar a los vivos.', 'Moriste durante una sesion en modo hardcore, asi que ahora eres un fantasma permanente.Intenta asustar a los vivos.', ''),
(@ENTRY+1, '|cff00ff00¡Congratulations, |cffffd700{} |cff00ff00!You have completed the first phase of the Hardcore challenge and have received a special title. Relax, have a beer, and enjoy the Classic content.|r', '', '', '', '', '', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado la primera fase del desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de Classic.|r', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado la primera fase del desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de Classic.|r', ''),
(@ENTRY+2, '|cff00ff00¡Congratulations, |cffffd700{} |cff00ff00!You have completed the second phase of the Hardcore challenge and have received a special title. Relax, have a beer, and enjoy the TBC content.|r', '', '', '', '', '', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado la segunda fase del desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de TBC.|r', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado la segunda fase del desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de TBC.|r', ''),
(@ENTRY+3, '|cff00ff00¡Congratulations, |cffffd700{} |cff00ff00!You have completed the Hardcore challenge and have received a special title. Relax, have a beer, and enjoy the WOTLK content.|r', '', '', '', '', '', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado el desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de WOTLK.|r', '|cff00ff00¡Felicidades, |cffffd700{} |cff00ff00!Has completado el desafio Hardcore y has recibido un titulo especial. Relajaos, tomad un cerveza y disfrutad del contenido de WOTLK.|r', ''),
(@ENTRY+4, '|cffff0000You already have this title.|r', '', '', '', '', '', '|cffff0000Ya tienes este titulo.|r', '|cffff0000Ya tienes este titulo.|r', ''),
(@ENTRY+5, '|cffff0000Error: Title with ID {} was not found.|r', '', '', '', '', '', '|cffff0000Error: No se encontro el titulo con ID {}.|r', '|cffff0000Error: No se encontro el titulo con ID {}.|r', ''),
(@ENTRY+6, 'Leaving the group... You can no longer be part of the living.', '', '', '', '', '', 'Saliendo del grupo... Ya no puedes formar parte de los vivos.', 'Saliendo del grupo... Ya no puedes formar parte de los vivos.', ''),
(@ENTRY+7, 'You will be a ghost forever...', '', '', '', '', '', 'Seras un fantasma para siempre...', 'Seras un fantasma para siempre...', ''),
(@ENTRY+8, 'A player killed you... in hardcore mode.', '', '', '', '', '', 'Un jugador te mato... en modo hardcore.', 'Un jugador te mato... en modo hardcore.', ''),
(@ENTRY+9, 'You died during a hardcore mode session... Skill issues.', '', '', '', '', '', 'Moriste durante una sesion en modo hardcore... Problemas de habilidad.', 'Moriste durante una sesion en modo hardcore... Problemas de habilidad.', ''),
